<?php\n// Define the service URL\n$wsdl = "http://example.com/service?wsdl";\n\n// Create a new SOAP client\n$client = new SoapClient($wsdl);\n\n// Define the long parameter as a string to avoid truncation\n$longValue = "9223372036854775807"; // Example of a 64-bit integer\n\n// Call the web service method\ntry {\n    $result = $client->YourServiceMethod(['longParameter' => $longValue]);\n    print_r($result);\n} catch (SoapFault $e) {\n    echo "Error: {$e->getMessage()}";\n}\n?>