<?php\nfunction hasValidGravatar($email) {\n    $emailHash = md5(strtolower(trim($email)));\n    $gravatarUrl = "https://www.gravatar.com/avatar/$emailHash?d=404";\n\n    $headers = @get_headers($gravatarUrl);\n    if (!$headers) {\n        return false; // Unable to fetch headers, consider as no Gravatar\n    }\n\n    // Check if the response is 200 OK\n    return strpos($headers[0], '200 OK') !== false;\n}\n\n// Example usage\n$email = "user@example.com";\nif (hasValidGravatar($email)) {\n    echo "Valid Gravatar found.";\n} else {\n    echo "No valid Gravatar found.";\n}\n?>