<?php\n// Initial arrays\n$measurements = array(1469, 1465, 739, 849);\n$diffs = array(0, 0, 0, 0);\n\n// Update the specific index of the $diffs array\n$diffs[2] = 200;\n\n// Loop through the $diffs array and subtract the values from the $measurements array\nforeach ($diffs as $index => $diff) {\n    $measurements[$index] -= $diff;\n}\n\n// Output the updated arrays\nprint_r($diffs);\nprint_r($measurements);\n?>