<?php\ninclude('handler_login.php');\n\n// Ensure the login script has processed successfully before continuing\nif (!isset($username)) {\n    exit("User not logged in");\n}\n\n// Process the form data\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $title = htmlspecialchars($_POST['title']);\n    $question = htmlspecialchars($_POST['question']);\n    $tags = htmlspecialchars($_POST['tags']);\n\n    // Process the question data as needed, e.g., store in the database\n    // E.g., $dbconn is available from handler_login.php\n\n    $sql = "INSERT INTO questions (title, question, tags, username) VALUES (\n        '".pg_escape_string($dbconn, $title)."',\n        '".pg_escape_string($dbconn, $question)."',\n        '".pg_escape_string($dbconn, $tags)."',\n        '".pg_escape_string($dbconn, $username)."'\n    );";\n\n    $result = pg_query($dbconn, $sql);\n\n    if ($result) {\n        echo "Question submitted successfully.";\n    } else {\n        echo "Error submitting question.";\n    }\n} else {\n    echo "Invalid request method.";\n}\n?>