function query($sql) {\n    $this->result = @mysqli_query($this->conn, $sql);\n    if (!$this->result) {\n        // Handle the error\n        throw new Exception("Database query failed: " . mysqli_error($this->conn));\n    }\n    return true;\n}\n\nfunction fetchassoc() {\n    return mysqli_fetch_assoc($this->result);\n}\n\nfunction convert() {\n    $this->db->open();\n    $this->db->query("SELECT * FROM ACCOUNTS");\n    $str = '';\n    while ($row = $this->db->fetchassoc()) {\n        $jobNum = $row['JOBNUMBER'];\n        $old_date = $row['INSTALLDATE'];\n        $new_date = date("Y-m-d", strtotime($old_date));\n        $sql_update = "UPDATE ACCOUNTS SET INSTALLDATE='$new_date' WHERE JOBNUMBER='$jobNum'";\n        \n        try {\n            $this->db->query($sql_update);\n        } catch (Exception $e) {\n            die($this->response->throwResponseError(MSG_DATABASE_ERROR . $e->getMessage()));\n        }\n        \n        $str .= $jobNum . " -- " . $new_date . "<br />";\n    }\n    return $str;\n}