class DPLS_Request {\n       private $_request;\n       private $_params;\n       private $_rurl;\n       private $_segment;\n       private $get_params;\n\n       public function __construct() {\n           $this->_rurl = $_SERVER['HTTP_HOST'];\n           $this->_segment = $this->extractSubdomain($this->_rurl);\n           $this->_request = $this->parseRequest();\n           $this->setDefaultControllerAndAction();\n       }\n\n       private function extractSubdomain($url) {\n           $parts = explode('.', $url);\n           // Assuming subdomain.shop.local structure\n           return (count($parts) > 2) ? $parts[0] : null;\n       }\n\n       private function parseRequest() {\n           $request = [];\n           $uri = trim($_SERVER['REQUEST_URI'], '/');\n           if ($uri) {\n               $segments = explode('/', $uri);\n               $request['controller'] = array_shift($segments);\n               $request['action'] = array_shift($segments);\n               $this->_params = $segments;\n           }\n           return $request;\n       }\n\n       private function setDefaultControllerAndAction() {\n           if ($this->_segment) {\n               if (empty($this->_request['controller'])) {\n                   $this->_request['controller'] = 'store'; // Default shop controller\n               }\n               if (empty($this->_request['action'])) {\n                   $this->_request['action'] = 'defaultAction'; // Default action in shop controller\n               }\n           } else {\n               if (empty($this->_request['controller'])) {\n                   $this->_request['controller'] = 'home'; // Default controller\n               }\n               if (empty($this->_request['action'])) {\n                   $this->_request['action'] = 'defaultAction'; // Default action in home controller\n               }\n           }\n       }\n\n       // Other methods...\n   }