<?php\n// Set the session save path (if not using the default)\n$sessionSavePath = session_save_path();\n\nif ($sessionSavePath == "") {\n    $sessionSavePath = sys_get_temp_dir();\n}\n\n$sessionFiles = scandir($sessionSavePath);\n\nforeach ($sessionFiles as $sessionFile) {\n    if (strpos($sessionFile, 'sess_') === 0) {\n        $sessionId = substr($sessionFile, 5);\n        session_id($sessionId);\n        session_start();\n        echo "Session ID: $sessionId\n";\n        print_r($_SESSION);\n        session_write_close(); // Close the session to avoid locking\n    }\n}\n?>