<?php\n// Define the WSDL URL\n$wsdl = "https://path-to-sabre-wsdl";\n\n// Define the SOAP options\n$options = array(\n    'trace' => 1,\n    'exceptions' => 1,\n    'login' => 'your-username',\n    'password' => 'your-password'\n);\n\ntry {\n    // Create the SOAP client\n    $client = new SoapClient($wsdl, $options);\n\n    // Define the request parameters\n    $params = array(\n        'Request' => array(\n            'SomeParameter' => 'Value'\n        )\n    );\n\n    // Make the SOAP call\n    $response = $client->__soapCall('YourOperationName', array($params));\n\n    // Output the response\n    print_r($response);\n} catch (SoapFault $fault) {\n    // Handle any errors\n    echo "Error: " . $fault->getMessage();\n}\n?>