$boundary = md5(date('U'));\n\n$to = $email;\n$subject = "My Subject";\n\n$headers = "From: myaddress@mydomain.com\r\n";\n$headers .= "X-Mailer: PHP/" . phpversion() . "\r\n";\n$headers .= "MIME-Version: 1.0\r\n";\n$headers .= "Content-Type: multipart/alternative; boundary=\"$boundary\"\r\n";\n\n$text = "You really ought remember the birthdays";\n$html = "\n<html>\n<head>\n<title>Birthday Reminders for August</title>\n</head>\n<body>\n<p>Here are the birthdays upcoming in August!</p>\n<table>\n<tr><th>Person</th><th>Day</th><th>Month</th><th>Year</th></tr>\n<tr><td>Joe</td><td>3rd</td><td>August</td><td>1970</td></tr>\n<tr><td>Sally</td><td>17th</td><td>August</td><td>1973</td></tr>\n</table>\n</body>\n</html>\n";\n\n$message = "--$boundary\r\n";\n$message .= "Content-Type: text/plain; charset=\"iso-8859-1\"\r\n";\n$message .= "Content-Transfer-Encoding: 7bit\r\n\r\n";\n$message .= "$text\r\n";\n$message .= "--$boundary\r\n";\n$message .= "Content-Type: text/html; charset=\"iso-8859-1\"\r\n";\n$message .= "Content-Transfer-Encoding: 7bit\r\n\r\n";\n$message .= "$html\r\n";\n$message .= "--$boundary--";\n\nmail($to, $subject, $message, $headers);