class My_Validate_DateFormat extends Zend_Validate_Abstract\n{\n    const INVALID_DATE = 'invalidDate';\n\n    protected $_messageTemplates = array(\n        self::INVALID_DATE => "'%value%' is not a valid date in the format dd/mm/yyyy"\n    );\n\n    public function isValid($value)\n    {\n        $this->_setValue($value);\n        $date = Zend_Date::isDate($value, 'dd/MM/yyyy');\n        if (!$date) {\n            $this->_error(self::INVALID_DATE);\n            return false;\n        }\n        return true;\n    }\n}