<?php\nrequire 'vendor/autoload.php';\n\nuse Kwn\NumberToWords\Language\English\Transformer\NumberTransformer;\n\nfunction convertWordsToNumbers($text) {\n    $numberTransformer = new NumberTransformer();\n    $words = explode(' ', $text);\n    $numberString = '';\n    \n    foreach ($words as $word) {\n        try {\n            $numberString .= $numberTransformer->toWords($word);\n        } catch (Exception $e) {\n            // If an exception occurs, it means the word is not a number\n            $numberString .= ' ' . $word;\n        }\n    }\n    \n    return $numberString;\n}\n\n$input = "iPhone has two hundred and thirty thousand seven hundred and eighty three apps";\n$output = convertWordsToNumbers($input);\necho $output;\n?>