class MyObj {\n    public function doSomethingWhichIsLogged() {\n        // ...\n        $logger = new Logger('/tmp/log.txt');\n        $logger->info('some message');\n        // ...\n    }\n}\n\nuse PHPUnit\Framework\TestCase;\n\nclass TestMyObj extends TestCase {\n    public function testDoSomethingWhichIsLogged() {\n        // Create a mock for the Logger class\n        $loggerMock = $this->createMock(Logger::class);\n\n        // Set up the expectation for the info() method to be called once\n        $loggerMock->expects($this->once())\n                   ->method('info')\n                   ->with('some message');\n\n        // Create the instance of MyObj\n        $myObj = new MyObj();\n\n        // Use reflection to set the private logger property\n        $reflection = new ReflectionClass($myObj);\n        $loggerProperty = $reflection->getProperty('logger');\n        $loggerProperty->setAccessible(true);\n        $loggerProperty->setValue($myObj, $loggerMock);\n\n        // Call the method to test\n        $myObj->doSomethingWhichIsLogged();\n    }\n}