<?php\nrequire_once('includes/MM_XSLTransform/MM_XSLTransform.class.php'); \n\n$asin = htmlspecialchars($_GET["asin"]);\n$restquery = "http://ecs.amazonaws.com/onca/xml?Service=AWSECommerceService&AWSAccessKeyId=[myid]&Operation=ItemLookup&ResponseGroup=Large&ItemId=" . $asin;\n\n$mm_xsl = new MM_XSLTransform();\n$mm_xsl->setXML($restquery);\n\n// Debug: Output the raw XML from AWS\n$xmlContent = file_get_contents($restquery);\nif ($xmlContent === FALSE) {\n    die("Error fetching XML data from AWS.");\n}\nheader('Content-Type: application/xml');\necho $xmlContent;  // Uncomment for debugging the raw XML content\n// exit();  // Uncomment to stop execution here for debugging\n\n$mm_xsl->setXSL("aws1.xsl");\n\n// Debug: Check for transformation errors\n$transformedContent = $mm_xsl->Transform();\nif ($transformedContent === FALSE) {\n    die("Error during XSL transformation.");\n}\n\necho $transformedContent;\n?>