use PHPUnit\Framework\TestCase;\n\nclass TestMyObj extends TestCase {\n    public function testDoSomethingWhichIsLogged() {\n        // Create a mock for the Logger class\n        $loggerMock = $this->createMock(Logger::class);\n\n        // Set up the expectation for the info() method to be called once\n        $loggerMock->expects($this->once())\n                   ->method('info')\n                   ->with('some message');\n\n        // Inject the mock logger into MyObj\n        $myObj = new MyObj($loggerMock);\n\n        // Call the method to test\n        $myObj->doSomethingWhichIsLogged();\n    }\n}