// Using mysqli with prepared statements\nfunction escape($data, $linkid) {\n    return mysqli_real_escape_string($linkid, trim($data));\n}\n\nfunction insertData($linkid, $data) {\n    $stmt = $linkid->prepare("INSERT INTO table_name (column_name) VALUES (?)");\n    $stmt->bind_param("s", $data);\n    $data = htmlspecialchars($data, ENT_QUOTES, 'UTF-8'); // Sanitize for HTML context\n    $stmt->execute();\n    $stmt->close();\n}\n\n// Example usage\n$linkid = new mysqli("hostname", "username", "password", "database");\n\nif ($linkid->connect_error) {\n    die("Connection failed: " . $linkid->connect_error);\n}\n\n$data = $_POST['data'];\ninsertData($linkid, $data);\n\n$linkid->close();