<?php\n   $host = "myip";\n   $user = "root";\n   $pass = "your_password";\n   $db = "mydatabase";\n\n   // Create connection\n   $conn = new mysqli($host, $user, $pass, $db);\n\n   // Check connection\n   if ($conn->connect_error) {\n       die("Connection failed: " . $conn->connect_error);\n   }\n\n   $sql = "SELECT COUNT(online) AS online_count FROM characters WHERE online=1";\n   $result = $conn->query($sql);\n\n   if ($result->num_rows > 0) {\n       // Output data of each row\n       while($row = $result->fetch_assoc()) {\n           echo "<br>Online: " . $row["online_count"] . "<br><br>";\n       }\n   } else {\n       echo "0 results";\n   }\n\n   $conn->close();\n   ?>