<?php\nfunction elapsedTimeInSeconds($time1, $time2) {\n    $start = new DateTime($time1);\n    $end = new DateTime($time2);\n    $interval = $start->diff($end);\n    return ($interval->h * 3600) + ($interval->i * 60) + $interval->s;\n}\n\n$t1 = "3:15:00";\n$t2 = "1:00:00";\n\n$elapsedSeconds = elapsedTimeInSeconds($t1, $t2);\n\necho $elapsedSeconds . "\n"; // Output will be in seconds\n?>