<?php\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    $id = $_POST['id'];\n    $html = urldecode($_POST['html']);\n\n    // Now you can safely insert $html into your MySQL database\n    // Example (using PDO for secure database interaction):\n    try {\n        $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'your_user', 'your_password');\n        $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n        $stmt = $pdo->prepare("INSERT INTO your_table (id, html_content) VALUES (:id, :html_content)");\n        $stmt->bindParam(':id', $id);\n        $stmt->bindParam(':html_content', $html);\n        $stmt->execute();\n        \n        echo "Data inserted successfully.";\n    } catch (PDOException $e) {\n        echo "Error: " . $e->getMessage();\n    }\n}\n?>