<?php\nrequire_once 'vendor/autoload.php'; // Assuming you are using Composer for MaxMind's GeoIP2 PHP API\n\nuse GeoIp2\Database\Reader;\n\n$reader = new Reader('/path/to/GeoLite2-Country.mmdb');\n\n$clientIp = $_SERVER['REMOTE_ADDR'];\n$record = $reader->country($clientIp);\n\n$allowedCountryCode = 'US'; // Replace with your desired country code\n\nif ($record->country->isoCode !== $allowedCountryCode) {\n    echo "Access restricted to users from " . $allowedCountryCode;\n    exit;\n}\n\n// Continue with your application logic\necho "Welcome user from " . $record->country->name;\n?>