<?php\n$cxmlString = <<<XML\n<root>\n    <item attribute="value">Content</item>\n</root>\nXML;\n\n// Load the XML string into a SimpleXMLElement object\n$xml = new SimpleXMLElement($cxmlString);\n\n// Get the value of the 'item' tag\n$itemContent = (string) $xml->item;\n\n// Get the value of the 'attribute' attribute\n$itemAttribute = (string) $xml->item['attribute'];\n\necho "Item Content: $itemContent\n";\necho "Item Attribute: $itemAttribute\n";\n?>