<?php\n// Path to the session directory\n$sessionPath = './session';\n\n// Maximum session file age in seconds (e.g., 24 hours)\n$maxFileAge = 86400; \n\n// Open the session directory\nif ($handle = opendir($sessionPath)) {\n    while (false !== ($file = readdir($handle))) {\n        $filePath = $sessionPath . DIRECTORY_SEPARATOR . $file;\n        // Check if the file is a session file and is old enough to be deleted\n        if (is_file($filePath) && (filemtime($filePath) + $maxFileAge < time())) {\n            unlink($filePath);\n        }\n    }\n    closedir($handle);\n}\n?>