<?php\n// Create a blank image\n$image = imagecreatetruecolor(200, 200);\n\n// Enable alpha blending\nimagealphablending($image, true);\n\n// Save full alpha channel information (to retain transparency)\nimagesavealpha($image, true);\n\n// Allocate a color with partial transparency (127 is fully transparent, 0 is fully opaque)\n$transparentColor = imagecolorallocatealpha($image, 0, 0, 0, 63); // 50% transparent black\n\n// Fill the image with the transparent color\nimagefill($image, 0, 0, $transparentColor);\n\n// Output the image to the browser\nheader('Content-Type: image/png');\nimagepng($image);\n\n// Free up memory\nimagedestroy($image);\n?>