<?php\n// Get today's date\n$today = new DateTime();\n\n// Get the start (Sunday) and end (Saturday) of this week\n$startOfWeek = clone $today;\n$startOfWeek->modify('last Sunday');\n\n$endOfWeek = clone $startOfWeek;\n$endOfWeek->modify('next Saturday');\n\n// Display the current week's date range\necho "Current week: " . $startOfWeek->format('Y-m-d') . " to " . $endOfWeek->format('Y-m-d') . "<br>";\n\n// Previous week\n$previousWeekStart = clone $startOfWeek;\n$previousWeekStart->modify('-1 week');\n\n$previousWeekEnd = clone $endOfWeek;\n$previousWeekEnd->modify('-1 week');\n\necho "Previous week: " . $previousWeekStart->format('Y-m-d') . " to " . $previousWeekEnd->format('Y-m-d') . "<br>";\n\n// Next week\n$nextWeekStart = clone $startOfWeek;\n$nextWeekStart->modify('+1 week');\n\n$nextWeekEnd = clone $endOfWeek;\n$nextWeekEnd->modify('+1 week');\n\necho "Next week: " . $nextWeekStart->format('Y-m-d') . " to " . $nextWeekEnd->format('Y-m-d') . "<br>";\n?>