function mymodule_wizard_form($form_state) {\n  $form = array();\n\n  // Define your form elements here\n  $form['step'] = array(\n    '#type' => 'value',\n    '#value' => isset($form_state['storage']['step']) ? $form_state['storage']['step'] : 1,\n  );\n\n  // Define other form elements\n\n  // Define the "Previous" button with a custom submit handler\n  $form['previous'] = array(\n    '#type' => 'submit',\n    '#value' => t('Previous'),\n    '#limit_validation_errors' => array(),\n    '#submit' => array('mymodule_wizard_form_previous_submit'),\n  );\n\n  // Define the "Next" button\n  $form['next'] = array(\n    '#type' => 'submit',\n    '#value' => t('Next'),\n  );\n\n  return $form;\n}\n\nfunction mymodule_wizard_form_previous_submit($form, &$form_state) {\n  // Decrease the step counter\n  $form_state['storage']['step']--;\n\n  // Rebuild the form without validation\n  $form_state['rebuild'] = TRUE;\n}