<?php\nif ($_SERVER['REQUEST_METHOD'] === 'POST' && !empty($_POST['xml'])) {\n    $xml_data = $_POST['xml'];\n\n    libxml_use_internal_errors(true);\n    $xml = simplexml_load_string($xml_data);\n\n    if ($xml === false) {\n        echo "Failed loading XML:\n";\n        foreach (libxml_get_errors() as $error) {\n            echo "\t", $error->message;\n        }\n    } else {\n        $credentials = $xml->Credentials;\n        $criterions = $xml->Criterions;\n\n        $username = $credentials->Username;\n        $password = $credentials->Password;\n        $orderNumber = $criterions->OrderNumber;\n        $startDate = $criterions->StartDate;\n        $endDate = $criterions->EndDate;\n\n        // Example of how to use these variables:\n        echo "Username: $username\n";\n        echo "Password: $password\n";\n        echo "OrderNumber: $orderNumber\n";\n        echo "StartDate: $startDate\n";\n        echo "EndDate: $endDate\n";\n\n        // Here you'd add your MySQL query logic to fetch orders between $startDate and $endDate.\n        // Make sure to use prepared statements to prevent SQL injection.\n    }\n}\n?>\n\n<form name="review" action="admin_xml.php" method="post">\n    <textarea name="xml" cols="40" rows="10"></textarea>\n    <input class="submit" type="submit" value="Submit Request">\n</form>