<?php\n$mysqli = new mysqli("localhost", "user", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Prepare a statement\n$stmt = $mysqli->prepare("INSERT INTO myTable (column1, column2) VALUES (?, ?)");\n$stmt->bind_param("ss", $param1, $param2);\n\n// Set parameters and execute\n$param1 = 'value1';\n$param2 = 'value2';\n$stmt->execute();\n\n// Reuse the prepared statement with different parameters\n$param1 = 'value3';\n$param2 = 'value4';\n$stmt->execute();\n\n// Close the statement and connection\n$stmt->close();\n$mysqli->close();\n?>