function encrypt($data, $key) {\n    $method = 'aes-256-cbc';\n    $iv = openssl_random_pseudo_bytes(openssl_cipher_iv_length($method));\n    $encrypted = openssl_encrypt($data, $method, $key, 0, $iv);\n    $encrypted = base64_encode($encrypted . '::' . $iv);\n    // Make the base64 URL-safe\n    $urlSafeEncrypted = strtr($encrypted, '+/=', '-_,');\n    return $urlSafeEncrypted;\n}\n\nfunction decrypt($data, $key) {\n    $method = 'aes-256-cbc';\n    // Revert the URL-safe base64 encoding\n    $data = strtr($data, '-_,', '+/=');\n    $data = base64_decode($data);\n    list($encrypted_data, $iv) = explode('::', $data, 2);\n    return openssl_decrypt($encrypted_data, $method, $key, 0, $iv);\n}\n\n// Example usage\n$key = 'your-secret-key';\n$data = 'user-authentication-data';\n\n$encryptedValue = encrypt($data, $key);\necho 'Encrypted URL-safe value: ' . $encryptedValue . PHP_EOL;\n\n$decryptedValue = decrypt($encryptedValue, $key);\necho 'Decrypted value: ' . $decryptedValue . PHP_EOL;