<?php\n// URL of the image\n$imageUrl = 'http://example.com/image.php';\n\n// Path where you want to save the image\n$saveTo = 'path/to/save/newimage.jpg';\n\n// Use file_get_contents to get the image data\n$imageData = file_get_contents($imageUrl);\n\n// Ensure the image data was successfully retrieved\nif ($imageData === FALSE) {\n    die('Error: Unable to fetch image from the URL.');\n}\n\n// Use file_put_contents to save the image data to a file\n$result = file_put_contents($saveTo, $imageData);\n\n// Check if the file was successfully written\nif ($result === FALSE) {\n    die('Error: Unable to save image to the specified path.');\n}\n\necho 'Image saved successfully!';\n?>