// Model\nclass User {\n    private $name;\n    private $avatar;\n\n    public function __construct($name, $avatar) {\n        $this->name = $name;\n        $this->avatar = $avatar;\n    }\n\n    public function getName() {\n        return $this->name;\n    }\n\n    public function getAvatar() {\n        return $this->avatar;\n    }\n}\n\n// View\nclass UserView {\n    public function displayUser($user) {\n        echo "Name: " . $user->getName() . "<br>";\n        echo "<img src='" . $user->getAvatar() . "' alt='Avatar'><br>";\n    }\n}\n\n// Controller\nclass UserController {\n    private $model;\n    private $view;\n\n    public function __construct($model, $view) {\n        $this->model = $model;\n        $this->view = $view;\n    }\n\n    public function updateView() {\n        $this->view->displayUser($this->model);\n    }\n}\n\n// Usage\n$model = new User("John Doe", "path/to/avatar.jpg");\n$view = new UserView();\n$controller = new UserController($model, $view);\n$controller->updateView();