// in lib/model/MyTable.php\n\nclass MyTable extends BaseMyTable\n{\n    public function setFulltextColumn($text)\n    {\n        // Use PostgreSQL function to generate the fulltext value\n        $fulltextValue = $this->generateFulltextValue($text);\n        // Set the column value\n        $this->setByName('FULLTEXT_COLUMN', $fulltextValue, BasePeer::TYPE_FIELDNAME);\n    }\n\n    private function generateFulltextValue($text)\n    {\n        // This is a placeholder function. Replace with actual Postgres fulltext function if necessary.\n        // Example using to_tsvector function\n        $pdo = Propel::getConnection();\n        $stmt = $pdo->prepare("SELECT to_tsvector('english', ?)");\n        $stmt->execute([$text]);\n        return $stmt->fetchColumn();\n    }\n}