<?php\n// Load the HTML content\n$html = file_get_contents('path/to/your/htmlfile.html');\n\n// Define the pattern to match the old JavaScript function\n$pattern = '/function\s+my_function\s*\(.*?\)\s*{.*?}/s';\n\n// Define the replacement function code\n$replacement = 'function new_function(hi) {\n    // do different stuff\n}';\n\n// Perform the replacement\n$newHtml = preg_replace($pattern, $replacement, $html);\n\n// Save the modified HTML content back to the file or output it\nfile_put_contents('path/to/your/htmlfile.html', $newHtml);\n?>