<?php\n// Hexadecimal value\n$hex = '60f347d15798c901';\n\n// Convert hex to decimal\n$decimal = hexdec($hex);\n\n// C# DateTime ticks start from 0001-01-01T00:00:00.000, 1 tick = 100 nanoseconds\n// Convert ticks to seconds (1 tick = 100 ns = 1e-7 seconds)\n$seconds = $decimal * 1e-7;\n\n// Create a DateTime object for the Unix epoch (1970-01-01T00:00:00.000)\n$epoch = new DateTime('0001-01-01', new DateTimeZone('UTC'));\n\n// Add the seconds to the epoch\n$interval = new DateInterval('PT' . $seconds . 'S');\n$epoch->add($interval);\n\n// Adjust for the difference between Unix epoch and C# DateTime epoch\n$epoch->sub(new DateInterval('P1970Y1M1D'));\n\n// Output the result\necho $epoch->format('Y-m-d H:i:s') . ' UTC';\n?>