<?php\n// Database connection\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Fetch the article type from the database\n$article_id = $_GET['article_id']; // Assume article_id is passed as a query parameter\n$sql = "SELECT article_type FROM articles WHERE id = ?";\n$stmt = $conn->prepare($sql);\n$stmt->bind_param("i", $article_id);\n$stmt->execute();\n$stmt->bind_result($selected_type);\n$stmt->fetch();\n$stmt->close();\n\n$types = ["news", "gossip", "travel", "performances", "others"];\n?>\n\n<!DOCTYPE html>\n<html>\n<head>\n    <title>Edit Article</title>\n</head>\n<body>\n    <form action="update_article.php" method="post">\n        <input type="hidden" name="article_id" value="<?php echo $article_id; ?>">\n        <label for="article_type">Article Type:</label>\n        <select name="article_type" id="article_type">\n            <?php foreach ($types as $type): ?>\n                <option value="<?php echo $type; ?>" <?php echo ($type == $selected_type) ? 'selected' : ''; ?>>\n                    <?php echo ucfirst($type); ?>\n                </option>\n            <?php endforeach; ?>\n        </select>\n        <input type="submit" value="Update Article">\n    </form>\n</body>\n</html>\n\n<?php\n$conn->close();\n?>