class MyClass {\n    public function __call($name, $arguments) {\n        echo "this is $name method and the value is " . $arguments[0];\n    }\n}\n\n$myclass = new MyClass();\n$myclass->name("aaa"); // Output: this is name method and the value is aaa\n$myclass->password("123"); // Output: this is password method and the value is 123\n$myclass->email("example@example.com"); // Output: this is email method and the value is example@example.com