<?php\n$fileToZip = 'path/to/yourfile.txt';\n$zipFileName = 'path/to/encrypted.zip';\n$password = 'your_secure_password';\n\n// Create a new zip archive\n$zip = new ZipArchive();\n\nif ($zip->open($zipFileName, ZipArchive::CREATE) === TRUE) {\n    // Add the file to the archive\n    $zip->addFile($fileToZip, basename($fileToZip));\n    \n    // Set the password for the zip archive\n    $zip->setPassword($password);\n    \n    // Encrypt the file\n    $zip->setEncryptionName(basename($fileToZip), ZipArchive::EM_AES_256);\n    \n    // Close the archive\n    $zip->close();\n    echo 'Encrypted zip archive created successfully.';\n} else {\n    echo 'Failed to create the encrypted zip archive.';\n}\n?>