$lines = file('english.php');\n$keys = array();\n$values = array();\n\nforeach($lines as $line) {\n    $matches = array();\n    if (preg_match('/DEFINE\(\'(.*?)\',\s*\'(.*)\'\);/i', $line, $matches)) {\n        $keys[] = $matches[1];\n        $values[] = $matches[2];\n    }\n}\n\nif (!empty($keys) && !empty($values)) {\n    $lang = array_combine($keys, $values);\n} else {\n    // Handle the error or initialize $lang as an empty array\n    $lang = array();\n    // Optionally, log an error message\n    error_log('No matches found in the file.');\n}\n\n// Now you can use $lang safely