<?php\n// Define the mapping of page parameters to file names\n$pages = [\n    'about' => 'about.php',\n    'help' => 'help.php',\n    'login' => 'login.php'\n];\n\n// Define the mapping for numeric identifiers to file names\n$dynamicPages = [\n    'shownews' => 'shownews.php',\n    'showuser' => 'showuser.php'\n];\n\n// Function to safely include a file if it exists in the mapping\nfunction includePage($page, $mapping) {\n    if (isset($page) && array_key_exists($page, $mapping)) {\n        include($mapping[$page]);\n    } else {\n        // Handle the error or include a default page\n        include('404.php');\n    }\n}\n\n// Check if 'page' parameter is set and include the corresponding file\nif (isset($_GET['page'])) {\n    includePage($_GET['page'], $pages);\n}\n\n// Check if 'shownews' or 'showuser' parameter is set and is a digit\nelseif (isset($_GET['shownews']) && ctype_digit($_GET['shownews'])) {\n    includePage('shownews', $dynamicPages);\n} elseif (isset($_GET['showuser']) && ctype_digit($_GET['showuser'])) {\n    includePage('showuser', $dynamicPages);\n} else {\n    // Handle the case where no valid parameters are passed\n    include('404.php');\n}\n?>