<?php\n// Define paths\n$swfFile = 'path/to/your/file.swf';\n$outputImage = 'path/to/output/frame.png';\n$thumbnail = 'path/to/output/thumbnail.jpg';\n\n// Extract the first frame as an image using swfrender\nexec("swfrender $swfFile -o $outputImage");\n\n// Check if output image was created\nif (file_exists($outputImage)) {\n    // Create a thumbnail using ImageMagick\n    $image = new Imagick($outputImage);\n    $image->thumbnailImage(200, 200, true); // Adjust dimensions as needed\n    $image->writeImage($thumbnail);\n    $image->clear();\n    $image->destroy();\n\n    echo 'Thumbnail created successfully: ' . $thumbnail;\n} else {\n    echo 'Failed to create thumbnail';\n}\n?>