<?php\n$xmlFile = 'path/to/your/large.xml';\n$outputFile = 'path/to/output.txt';\n\n// Open the output file for writing\n$outputHandle = fopen($outputFile, 'w');\nif (!$outputHandle) {\n    die("Unable to open output file");\n}\n\n// Create XMLReader instance\n$reader = new XMLReader();\n$reader->open($xmlFile);\n\n// Define the tag you are looking for\n$targetTag = 'your_target_tag';\n\nwhile ($reader->read()) {\n    if ($reader->nodeType == XMLReader::ELEMENT && $reader->localName == $targetTag) {\n        $content = $reader->readInnerXML();\n        fwrite($outputHandle, $content . PHP_EOL);\n    }\n}\n\n$reader->close();\nfclose($outputHandle);\n\necho "Extraction completed successfully.";\n?>