$currentVideoId = 234; // This should be dynamically set based on the current video\n\n// Query to get the previous video ID\n$prevQuery = "SELECT videoId FROM videos WHERE videoId < ? AND IsActive = 1 ORDER BY videoId DESC LIMIT 1";\n$stmt = $mysqli->prepare($prevQuery);\n$stmt->bind_param("i", $currentVideoId);\n$stmt->execute();\n$stmt->bind_result($prevVideoId);\n$stmt->fetch();\n$stmt->close();\n\n// Query to get the next video ID\n$nextQuery = "SELECT videoId FROM videos WHERE videoId > ? AND IsActive = 1 ORDER BY videoId ASC LIMIT 1";\n$stmt = $mysqli->prepare($nextQuery);\n$stmt->bind_param("i", $currentVideoId);\n$stmt->execute();\n$stmt->bind_result($nextVideoId);\n$stmt->fetch();\n$stmt->close();\n\n// Output the previous and next video IDs\necho "Previous Video ID: " . $prevVideoId . "<br>";\necho "Next Video ID: " . $nextVideoId . "<br>";