<?php\nfunction readability($text) {\n    // Count sentences using regular expressions\n    $sentence_endings = '/[.!?]/';\n    $total_sentences = preg_match_all($sentence_endings, $text, $matches);\n\n    // Count words\n    $total_words = str_word_count($text);\n\n    // Count syllables\n    $total_syllables = count_syllables($text);\n\n    // Calculate the Flesch-Kincaid Readability Score\n    $score = 206.835 - (1.015 * ($total_words / $total_sentences)) - (84.6 * ($total_syllables / $total_words));\n\n    return $score;\n}\n\nfunction count_syllables($text) {\n    $words = str_word_count($text, 1);\n    $total_syllables = 0;\n\n    foreach ($words as $word) {\n        // Count vowels in the word\n        $word = preg_replace('/[^a-z]/i', '', $word); // Remove non-alphabetic characters\n        $syllables = preg_match_all('/[aeiouy]+/i', $word);\n        // Subtract silent 'e' at the end\n        if (preg_match('/[aeiouy]e\b/i', $word)) {\n            $syllables--;\n        }\n        // Ensure at least one syllable per word\n        $total_syllables += max($syllables, 1);\n    }\n\n    return $total_syllables;\n}\n\n// Example usage\n$text = "This is a simple example of text. It includes multiple sentences. How readable is it?";\necho readability($text);\n?>