<?php\n// Example machine keys\n$validationKey = 'YOUR_VALIDATION_KEY';\n$decryptionKey = 'YOUR_DECRYPTION_KEY';\n\n// Example encrypted ticket from the cookie\n$encryptedTicket = $_COOKIE['.ASPXAUTH'];\n\n// Base64 decode the encrypted ticket\n$decodedData = base64_decode($encryptedTicket);\n\n// Decrypt the decoded data (assuming AES encryption)\n$decryptionKey = hex2bin($decryptionKey);\n$iv = substr($decodedData, 0, 16);\n$ciphertext = substr($decodedData, 16);\n\n$decryptedData = openssl_decrypt($ciphertext, 'aes-256-cbc', $decryptionKey, OPENSSL_RAW_DATA, $iv);\n\n// Now $decryptedData contains the plaintext ticket\necho $decryptedData;\n\n// Note: You would need to parse the decrypted ticket to extract the session variables\n?>