<?php\n// Read the raw POST data\n$rawPostData = file_get_contents('php://input');\n\n// Load the XML data into a SimpleXMLElement object\n$xml = simplexml_load_string($rawPostData);\n\nif ($xml === false) {\n    // Handle error if the XML couldn't be parsed\n    echo "Failed to parse XML";\n} else {\n    // Process the XML data\n    echo "Received XML data: ";\n    print_r($xml);\n}\n?>