<?php\n\n$username = "firstleg_floa";\n$password = "**";\n$hostname = "***1";\n$db = "firstleg_bank";\n$guildUser = strtolower('grissiom');\n\n$mysqli = new mysqli($hostname, $username, $password, $db);\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$query = "SELECT * FROM Bank WHERE userId = ?";\n$stmt = $mysqli->prepare($query);\n$stmt->bind_param('s', $guildUser);\n$stmt->execute();\n$result = $stmt->get_result();\n\n$rsArr = array();\n$i = 0;\n\nwhile ($row = $result->fetch_assoc()) {\n    $rsArr[$i]['userId'] = $row['userId'];\n    $rsArr[$i]['item'] = $row['item'];\n    $rsArr[$i]['amount'] = $row['amount'];\n    $rsArr[$i]['position'] = $row['position'];\n    $i++;\n}\n\n$stmt->close();\n$mysqli->close();\n?>\n\n<?php if($i > 0) { ?>\n    <?= "ghdfdgdfg " . $rsArr[0]['item']; ?> ----\n    <?= $rsArr[0]['userId']; ?>\n    <table>\n        <tr><td>Item</td><td>Amount</td></tr>\n        <?php for ($x = 0; $x < $i; $x++) { ?>\n            <tr><td><?= $rsArr[$x]['item']; ?></td><td><?= $rsArr[$x]['amount']; ?></td></tr>\n        <?php } ?>\n    </table>\n<?php } else { ?>\n    <p>No results found.</p>\n<?php } ?>