<?php\n// Database connection\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_dbname";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Function to get top 10 keywords for a category\nfunction getTopKeywords($categoryId) {\n    global $conn;\n    \n    // Retrieve all texts for the category\n    $sql = "SELECT text FROM texts WHERE category_id = ?";\n    $stmt = $conn->prepare($sql);\n    $stmt->bind_param("i", $categoryId);\n    $stmt->execute();\n    $result = $stmt->get_result();\n    \n    $words = [];\n    \n    while ($row = $result->fetch_assoc()) {\n        $text = strtolower($row['text']);\n        $textWords = str_word_count($text, 1);\n        \n        // Count word frequency\n        foreach ($textWords as $word) {\n            if (isset($words[$word])) {\n                $words[$word]++;\n            } else {\n                $words[$word] = 1;\n            }\n        }\n    }\n    \n    // Sort words by frequency\n    arsort($words);\n    \n    // Get top 10 words\n    $topWords = array_slice(array_keys($words), 0, 10);\n    \n    return $topWords;\n}\n\n// Example usage\n$categoryId = 1; // Replace with your category ID\n$topKeywords = getTopKeywords($categoryId);\n$metaKeywords = implode(", ", $topKeywords);\n\necho "<meta name='keywords' content='$metaKeywords'>";\n\n// Close the connection\n$conn->close();\n?>