// Load the CodeIgniter framework (assuming this script is within the application directory)\nrequire_once 'path_to_codeigniter/index.php';\n\n// Start session and get CodeIgniter instance\n$CI =& get_instance();\n$CI->load->library('session');\n\n// Get the user ID from the session\n$user_id = $CI->session->userdata('user_id');\n\nif ($user_id) {\n    echo "Logged in user's ID: " . $user_id;\n} else {\n    echo "User is not logged in.";\n}