// Include aMember API\nrequire_once '/path/to/amember/library/Am/Lite.php';\n\n// Check if user is logged in\nif (Am_Lite::getInstance()->isLoggedIn()) {\n    // Check if user has access to a specific product\n    if (Am_Lite::getInstance()->haveSubscriptions(1)) {\n        // User has access to premium content\n        echo 'Welcome to premium content!';\n    } else {\n        // User does not have access\n        echo 'Please subscribe to access premium content.';\n    }\n} else {\n    // User is not logged in\n    echo 'Please log in to access premium content.';\n}