<?php\n// Iterate through the week from Monday to Sunday\nfor ($i = 0; $i < 7; $i++) {\n    $currentDate = clone $startOfWeek;\n    $currentDate->modify("+$i day");\n    $currentDateStr = $currentDate->format('Y-m-d');\n\n    echo "<p>" . $currentDate->format('D, Y-m-d') . "</p>";\n\n    // Check if there's data for the current date\n    if (isset($data[$currentDateStr])) {\n        // Display your data here\n        echo "<p>Data: " . $data[$currentDateStr]['your_data_column'] . "</p>";\n    } else {\n        echo "<p>No data available</p>";\n    }\n}\n?>