<?php\n// Define file paths\n$rtfFile = 'path/to/your/file.rtf';\n$pdfFile = 'path/to/your/output.pdf';\n$jpgFile = 'path/to/your/output.jpg';\n\n// Convert RTF to PDF using unoconv\nexec("unoconv -f pdf -o " . escapeshellarg($pdfFile) . " " . escapeshellarg($rtfFile), $output, $return_var);\nif ($return_var !== 0) {\n    die("Error converting RTF to PDF: " . implode("\n", $output));\n}\n\n// Convert PDF to JPG using ImageMagick\nexec("magick convert " . escapeshellarg($pdfFile) . " " . escapeshellarg($jpgFile), $output, $return_var);\nif ($return_var !== 0) {\n    die("Error converting PDF to JPG: " . implode("\n", $output));\n}\n\necho "RTF file successfully converted to JPG.";\n?>