<?php\n\nclass Product {\n    public static $static_type = 'product';\n    public static $static_table = 'product_table';\n}\n\nclass News {\n    public static $static_type = 'news';\n    public static $static_table = 'news_table';\n}\n\nclass Events {\n    public static $static_type = 'events';\n    public static $static_table = 'events_table';\n}\n\nclass Conf {\n    public static function get_class_array() {\n        return ['Product', 'News', 'Events'];\n    }\n}\n\nforeach (Conf::get_class_array() as $class) {\n    $reflection = new ReflectionClass($class);\n    echo $reflection->getStaticPropertyValue('static_type') . "\n";\n}\n?>